import React, {useState, useEffect, Fragment} from 'react';
import {Link} from 'react-router-dom';
import axios from 'axios';
import logo from '../icons/dog.svg';

const DoggoList = props => {
  const [doggo, setDoggo] = useState ({data: []});
  const [isLoading, setIsLoading] = useState (true);

  const getData = async () => {
    await axios
    .get(props.urlApi)
    .then(response => {
      console.log()
      setDoggo(response.data);
      setIsLoading(false)
    })  
    .catch(error => {
      console.log(error)
    })  
  };

  const deleteData = async (id) => {
    await axios
      .delete (props.urlApi + id, doggo)
      .then (response => {
        console.log (response);
        console.log (response.data);
        alert ("Id " + doggo.id + " is sucessfully deleted");
      })
      .catch (error => {
        console.log (error);
        alert (error);
      });
  } 

  useEffect(() => {
    getData();
  }, []);

  const handleSubmit = event => {
    event.preventDefault (); 
    deleteData(doggo.id);
  };

  return (
    <Fragment>
      {isLoading
        ? 'Loading the data ... '
        : doggo.data.map (doggo => (
            <div className="doggo-list-box">
              <div className="columns">
                <div className="column">
                  <img src={logo} alt="logo" width="112" height="28" />
                </div>

                <div className="column is-8" key={doggo.id}>
                  <p><strong>{doggo.name}</strong></p>
                  <p>Age: {doggo.age}</p>
                  <p>Description: {doggo.description}</p>
                  <p>
                    Doggo id:
                    {doggo.doggo_id.first + '...' + doggo.doggo_id.last}
                  </p>
                </div>

                <div className="column">
                  <Link to="/doggo-registration/edit">
                    <button className="button">Edit</button>
                  </Link>
                  <form onSubmit={handleSubmit}>
                    <button className="button">{doggo.id}</button>
                  </form>


                </div>
              </div>
            </div>
          ))}
    </Fragment>
  );
};

export default DoggoList;
